    public static int rangeSum(int[] nums, int n, int left, int right) {
        int pointerLeft = 0;
        int pointerRight = 0;
        int[] newArr = new int[n * (n + 1) / 2];
        int i = 0;
        int total = 0;
        //Find the subarray
        while (pointerLeft < n) {

            total += nums[pointerRight];

            newArr[i] = total;


            pointerRight++;
            i++;
            if (pointerRight == n) {
                pointerLeft++;
                pointerRight = pointerLeft;
                total = 0;
            }
        }
        //Sort array
        for (int j = 1; j < (n * (n + 1) / 2); j++) {
            int temp = newArr[j];
            int k = j - 1;
            while ((k >= 0) && (temp < newArr[k])) {
                newArr[k+1] = newArr[k];
                k--;
            }
            newArr[k+1]= temp;
        }

        //Find the range
        int sum = 0;
        for (int j = left-1; j < right; j++) {
            sum += newArr[j];
        }

        return sum;
    }
